template.sourcecode:
{% extends "master.tpl" %}

{% block main %}
  {% set entries = apps.recent(10) %}
  {% for entry in entries %}
  <article>
    <h1><a href="{{ entry.url }}">{{ entry.title }}</a></h1>
    {{ entry.body|markdown }}
    <p class="meta">作成:{{ entry.create_date|date('Y-m-d H:i') }}&nbsp;&nbsp;更新:{{ entry.modify_date|date('Y-m-d H:i') }}</p>
    {% if not loop.last %}<hr>{% endif %}  
  </article>
  {% endfor %}
{% endblock %}

{% extends 'master.tpl' %}

{% block main %}
  {% set entries = apps.entries(current.id) %}
  {% for entry in entries %}
    <article>
      <h1><a href="{{ entry.url }}">{{ entry.title }}</a></h1>
      {{ entry.body|markdown }}
      <p class="meta">作成:{{ entry.create_date|date('Y-m-d H:i') }}&nbsp;&nbsp;更新:{{ entry.modify_date|date('Y-m-d H:i') }}</p>
      {% if not loop.last %}<hr>{% endif %}
    </article>
  {% endfor %}
{% endblock %}

{% extends 'master.tpl' %}

{% macro attachments(files) %}
  {% for file in files %}
    {% if loop.first %}
      <div class="attachments">
        <ul>
    {% endif %}
    {% if 'image/' in file.mime %}
      <li class="image"><img src="{{ file.path }}" alt="{{ file.alternate }}">
    {% else %}
      <li class="file">
        <a href="{{ file.path }}" target="_blank">{{ file.alternate }}</a>
    {% endif %}
    {% if file.note is not empty %}
      <div class="caption">{{ file.note|nl2br }}</div>
    {% endif %}
    </li>
    {% if loop.last %}
        </ul>
      </div>
    {% endif %}
  {% endfor %}
{% endmacro attachments %}

{% macro recursion(eid, i) %}
  {% import _self as self %}
  {% set sections = apps.sections(eid,i) %}
  {% for item in sections %}
    <section>
      <h{{ item.level }}>{{ item.title }}</h{{ item.level }}>
      <div class="detail">
        {{ item.body|markdown }}
        {{ self.attachments(apps.attachments(item.id,'section')) }}
      </div>
      {% if item.id is not empty %}
        {{ self.recursion(eid,item.id) }}
      {% endif %}
    </section>
  {% endfor %}
{% endmacro recursion %}

{% block main %}
{% import _self as self %}
<article>
  <h1>{{ current.title }}</h1>
  <div class="detail">
    {{ current.body|markdown }}
    {{ self.attachments(apps.attachments(current.id)) }}
  </div>
  {{ self.recursion(current.identifier, null) }}
</article>
{% endblock %}

{% macro recursion(i) %}
  {% import _self as self %}
  {% set category = apps.filterCategories(i) %}
  {% for item in category %}
    {% if loop.first %}
      <ul>
    {% endif %}
    <li>{% if item.template is not empty %}<a href="{{ item.url }}">{{ item.title }}</a>{% else %}{{ item.title }}{% endif %}
    {% if item.id is not null %}
      {{ self.recursion(item.id) }}
    {% endif %}
    </li>
    {% if loop.last %}
      </ul>
    {% endif %}
  {% endfor %}
{% endmacro %}

